async function onSuccess(randomName) {
  const incidentName = randomName;

  /**
   * Trigger a PagerDuty incident. This will page people according to the 
   * Escalation Policy of the Service that the reporter selected.
   */
	const createdPdIncident = await helper_createPagerDutyIncident.trigger({
    additionalScope: {
      incidentKey: incidentName,
    }
  });
  if (!createdPdIncident) {
    // Fall back to error handling in the createPagerDutyIncident Query itself.
    return;
  } else {
		// If successful, show the resultContainer and hide the reportForm, by toggling this state:
    stateWasIncidentReported.setValue(true);
  }

  const incidentAssigneeNames = createdPdIncident.incident.assignments.map((assignment) => {
    return assignment.assignee.summary;
  });

  // The logic below sets the incident reporter as the initial Incident Commander.
  // (You may choose to change this. For example, you might decide to set up a
  // separate PagerDuty rotation for Incident Commanders.)
  const incidentCommanderEmail = current_user.email;

  /**
   * Create an incident Slack channel.
   */
  const createdChannelId = await _makeSlackChannel(incidentName, incidentAssigneeNames);

  if (createdChannelId) {
    const slackUsers = await _getRespondersAsSlackUsers(createdPdIncident, incidentCommanderEmail);
    _inviteRespondersToSlackChannel(
      incidentAssigneeNames,
      slackUsers,
      createdChannelId,
      incidentName
    );
    _setSlackChannelTopic(
      slackUsers,
      incidentCommanderEmail,
      createdChannelId
    );
  }

  /*
   * Write the information to your database.
   */
  recordIncidentToDatabase.trigger({
    additionalScope: {
      id: incidentName,
      pagerDutyId: createdPdIncident.incident.id,
      pagerDutyUrl: createdPdIncident.incident.html_url,
      slackChannelId: createdChannelId,
      slackChannelName: incidentName,
      slackChannelUrl: createdChannelId ? window.SLACK.createDeepLink(createdChannelId) : '',
      reporterEmail: current_user.email,
      commanderEmail: incidentCommanderEmail,
  	}
  }).then(() => {
    // If successful, do nothing.
  }, (error) => {
    stateNonBlockingErrors.push(
      `Did not write the incident to the incident database. Please manually record it.`
    );
    console.log('Error when writing to incident database:');
    console.log(error);
  });
}

/*
 * Creates the Slack channel with the specified name.
 * 
 * @return String The ID of the created Slack channel.
 */
async function _makeSlackChannel(slackChannelName, incidentAssigneeNames) {
  const createChannelResp = await helper_createSlackChannel.trigger({
    additionalScope: {
      incidentKey: slackChannelName,
    }
  });
  const createdChannelId = createChannelResp?.ok ? createChannelResp?.channel?.id : null;

  if (createdChannelId == null) {
    stateNonBlockingErrors.value.push(
      `Did not create Slack channel. Please manually create the Slack channel named #${slackChannelName} and invite the following people: ${incidentAssigneeNames.join(', ')}`
    );
    console.log('Slack error when creating channel:');
    console.log(createChannelResp);
  }
  return createdChannelId;
}

/*
 * This function identifies the responders, and returns their Slack User identities.
 * 
 * This code assumes that each person's email in PagerDuty matches their email in Slack.
 *
 * @return Array<Object> An Array in which each object is a User, as defined by the 
 *   docs here: https://api.slack.com/methods/users.lookupByEmail
 */
async function _getRespondersAsSlackUsers(createdPdIncident, incidentCommanderEmail) {
  const incidentAssignees = createdPdIncident.incident.assignments.map((assignment) => {
    return assignment.assignee.id;
  });
  const assigneeDetails = await Promise.all(
    incidentAssignees.map((responderId) => {
      return helper_getPagerDutyUser.trigger({
        additionalScope: {
          pdUserId: responderId,
        }
      });
    })
  );

  // Note: It's possible the incident reporter is among the PagerDuty responders, so we use a Set here to deduplicate just in case.
  const responderEmails = new Set(assigneeDetails.map(assigneeDetail => assigneeDetail?.user?.email));
  responderEmails.add(incidentCommanderEmail);

  const slackUsers = await Promise.all(
    Array.from(responderEmails).map((personEmail) => {
      return helper_lookupSlackUserByEmail.trigger({
        additionalScope: {
					userEmail: personEmail,
        }
      });
    })
  );

  return _.compact(
    slackUsers.map((slackUser) => {
      if (!slackUser.ok) {
        return null;
      }
      return slackUser.user;
    })
  );
}

/*
 * Adds the Slack Users to the specified Slack channel.
 * @return Boolean Whether the people were successfully invited.
 */
async function _inviteRespondersToSlackChannel(incidentAssigneeNames, slackUsers, slackChannelId, slackChannelName) {
  const slackUserIds = slackUsers.map(slackUser => slackUser.id);
  const inviteResponse = await helper_invitePeopleToSlackChannel.trigger({
    additionalScope: {
      channelId: slackChannelId,
      userIds: slackUserIds.join(','),
    }
  });
  if (!inviteResponse.ok) {
    stateNonBlockingErrors.push(
      `Did not invite responders to Slack channel. Please manually invite the following people to the Slack channel named  #${slackChannelName}: ${incidentAssigneeNames.join(', ')}`
    );
    console.log('Slack error when inviting responders:');
    console.log(inviteResponse);
  }

  return inviteResponse.ok;
}

/**
 * Sets the description of the Slack channel to a summary of the incident and the responders.
 * @return Boolean Whether the operation was successful.
 */
async function _setSlackChannelTopic(slackUsers, incidentCommanderEmail, slackChannelId) {
  const incidentCommanderSlackId = slackUsers.find((slackUser) => {
    return slackUser.profile.email === incidentCommanderEmail;
  })?.id;
  const otherPagedResponderIds = slackUsers.filter((slackUser) => {
    return slackUser.profile.email !== incidentCommanderEmail;
  }).map((slackUser) => slackUser.id);

  const incidentCommanderSlackIdFormattedAsLink = `<@${incidentCommanderSlackId}>`;
  const otherPagedRespondersFormattedAsLinks = otherPagedResponderIds.map(id => `<@${id}>`).join(', ');

  const topic = [
    `Incident: ${titleInput.value}`,
    `Incident commander: ${incidentCommanderSlackIdFormattedAsLink}`,
    `Other assignees: ${otherPagedRespondersFormattedAsLinks}`,
    `Details: ${descriptionInput.value}`
  ].join('\n');

  // We can run this operation in parallel with the write to the database (below), 
  // so we don't "await" this operation.
  const setTopicResp = await helper_setSlackChannelTopic.trigger({
    additionalScope: {
      channelId: slackChannelId,
      topicText: topic,
    }
  });

  if (!setTopicResp.ok) {
    stateNonBlockingErrors.push(
      `Did not set the Slack channel description. Please manually go to the incident Slack channel and set the description to: ${topic}`
    );
    console.log('Slack error when setting channel topic:')
    console.log(setTopicResp);
  }
  return setTopicResp.ok;
}



