page_view_events as (
  select
    "userId" as user_id,
    date("createdAt") as day,
    'PAGE_VIEW' as event_type,
    count(1) as event_cnt
  from audit_trail_events
  where
    "actionType" = 'PAGE_VIEW'
    and date("createdAt") > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and date("createdAt") <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
    and "organizationId" = ANY({{organizationIds.data}}) -- this is necessary to hit (organizationId, createdAt) index  
    and "userId" is not null
  group by 1, 2, 3
),
page_save_events as (
  select
    "userId" as user_id,
    date("createdAt") as day,
    'PAGE_SAVE' as event_type,
    count(1) as event_cnt
  from page_saves
  where 
    date("createdAt") > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and date("createdAt") <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
    and "userId" is not null  
  group by 1, 2, 3
),
workflow_save_events as (
  select
    "createdBy" as user_id,
    date("createdAt") as day,
    'WORKFLOW_SAVE' as event_type,
    count(1) as event_cnt
  from workflow_save
  where 
    date("createdAt") > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and date("createdAt") <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
    and "createdBy" is not null  
  group by 1, 2, 3
),
all_events as (
  select 
    *
  from page_view_events
  union all
  select 
    *
  from page_save_events
  union all
  select
    *
  from workflow_save_events  
),
user_types_current as (
  select
    user_id,
    true as is_current,
    case 
      when sum(case when event_type IN ('PAGE_SAVE', 'WORKFLOW_SAVE') then event_cnt else 0 end) >= 5 then 'builder'
      when sum(case when event_type = 'PAGE_VIEW' then event_cnt else 0 end) > 0 then 'enduser'
      else 'none' 
    end as user_type
  from all_events
  where
    day > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '30 days') 
    and day <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
  group by user_id
),
user_types_previous as (
  select
    user_id,
    false as is_current,
    case 
      when sum(case when event_type IN ('PAGE_SAVE', 'WORKFLOW_SAVE') then event_cnt else 0 end) >= 5 then 'builder' 
      else 'enduser' 
    end as user_type
  from all_events
  where
    day > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and day <= (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '30 days') 
  group by user_id
)
select
  coalesce(sum(case when user_type = 'builder' and is_current then 1 else 0 end), 0) as "countBuilders",
  coalesce(sum(case when user_type = 'enduser' and is_current then 1 else 0 end), 0) as "countEndUsers",
  coalesce(sum(case when user_type = 'builder' and not is_current then 1 else 0 end), 0) as "countPrevBuilders",
  coalesce(sum(case when user_type = 'enduser' and not is_current then 1 else 0 end), 0) as "countPrevEndUsers"
from (
  select * from user_types_current union all select * from user_types_previous
) as all_user_types
