^3",[]],"^;",["^0",["^ ","n","position2","v",["^ ","^?","container4","^D","body","^E","bbeb8","row",0,"col",9,"^F",0.6,"^G",3,"^H",0]]],"^<",null,"^=",null,"^>",null,"^?","","^@","~m1693341540017","^A","~m1693597229516","^B","","^C",null]]],"downloadUsers",["^0",["^ ","n","pluginTemplate","v",["^ ","id","downloadUsers","^4","datasource","^5","SqlQueryUnified","^6",null,"^7","91949560-c1a0-41ad-99e2-9557982af823","^8","Retool Audit Log","^9",["^3",["queryRefreshTime","","records","","lastReceivedFromResourceAt",null,"databasePasswordOverride","","queryDisabledMessage","","servedFromCache",false,"offlineUserQueryInputs","","successMessage","","queryDisabled","","playgroundQuerySaveId","latest","workflowParams",null,"resourceNameOverride","","runWhenModelUpdates",false,"workflowRunId",null,"showFailureToaster",true,"query","with
page_view_events as (
  select
    "userId" as user_id,
    date("createdAt") as day,
    'PAGE_VIEW' as event_type,
    count(1) as event_cnt
  from audit_trail_events
  where
    "actionType" = 'PAGE_VIEW'
    and date("createdAt") > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and date("createdAt") <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
    and "organizationId" = ANY({{organizationIds.data}}) -- this is necessary to hit (organizationId, createdAt) index 
    and "userId" is not null
  group by 1, 2, 3
),
page_save_events as (
  select
    "userId" as user_id,
    date("createdAt") as day,
    'PAGE_SAVE' as event_type,
    count(1) as event_cnt
  from page_saves
  where 
    date("createdAt") > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and date("createdAt") <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
    and "userId" is not null  
  group by 1, 2, 3
),
workflow_save_events as (
  select
    "createdBy" as user_id,
    date("createdAt") as day,
    'WORKFLOW_SAVE' as event_type,
    count(1) as event_cnt
  from workflow_save
  where 
    date("createdAt") > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '60 days') 
    and date("createdAt") <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
    and "createdBy" is not null  
  group by 1, 2, 3
),
all_events as (
  select 
    *
  from page_view_events
  union all
  select 
    *
  from page_save_events
  union all
  select
    *
  from workflow_save_events  
),
user_types as (
  select
    email,
    "firstName" as first_name,
    "lastName" as last_name,
    case 
      when sum(case when event_type IN ('PAGE_SAVE', 'WORKFLOW_SAVE') then event_cnt else 0 end) >= 5 then 'standard_user'
      when sum(case when event_type = 'PAGE_VIEW' then event_cnt else 0 end) > 0 then 'end_user'
      else 'none' 
    end as user_type
  from all_events
  JOIN users ON all_events.user_id=users.id
  where
    day > (to_date({{ DateRange.value.end }}, 'YYYY-MM-DD') - INTERVAL '30 days') 
    and day <= to_date({{ DateRange.value.end }}, 'YYYY-MM-DD')
  group by email, "firstName", "lastName"
)
select
  *,
  MD5(email) as hashed_email
from (
  select * from user_types
) as all_user_types
